mapper.update:

UPDATE  BOARD
SET     BOARD_TITLE = #{BOARD_TITLE},
        BOARD_CONTENT = #{BOARD_CONTENT}
WHERE   BOARD_NUM = #{BOARD_NUM}


UPDATE	FILES
SET		FILES_DEL = 'N'
WHERE	FILES_NUM = #{FILE_IDX}


UPDATE  BOARD
SET     BOARD_DEL_GB = 'Y'
WHERE	BOARD_NUM = #{BOARD_NUM}


UPDATE	FILES
SET		FILES_DEL = 'Y'
WHERE	FILES_PARENT = #{IDX}
