mapper.select:

SELECT  MEM_ID
FROM    MEMBER
WHERE   MEM_NAME = #{MEM_NAME}
AND     MEM_EMAIL = #{MEM_EMAIL}
AND     MEM_DEL_GB = 'N'


SELECT  COUNT(*)
FROM    MEMBER
WHERE   MEM_NAME = #{MEM_NAME}
AND     MEM_EMAIL = #{MEM_EMAIL}
AND     MEM_DEL_GB = 'N'


SELECT  COUNT(*)
FROM    MEMBER
WHERE   MEM_ID = #{MEM_ID}
AND     MEM_EMAIL = #{MEM_EMAIL}
AND     MEM_DEL_GB = 'N'

mapper.update:

    UPDATE  MEMBER
	SET     MEM_PW = #{authCode}
	WHERE   MEM_ID = #{MEM_ID}
	AND     MEM_EMAIL = #{MEM_EMAIL}
	AND     MEM_DEL_GB = 'N'
